public class ForPattern {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for(int i = 1;i<=4;i++)
		{
			for (int j = 0; j < 4; j++) {
				System.out.print("*");
				
			}
			System.out.println();
		}
		
	}

}
